import React, { useState } from "react";
import MReco1 from "./MReco1";
import MReco2 from "./MReco2";

function FavoritePage() {
  const [favoritedItems, setFavoritedItems] = useState([]);

  // Function to remove item from favorites
  const removeFromFavorites = (item) => {
    const updatedItems = favoritedItems.filter((favItem) => favItem !== item);
    setFavoritedItems(updatedItems);
  };

  return (
    <div className="favorite-page">
      <h2>Favorite Items</h2>
      <div className="favorite-items">
        {favoritedItems.map((item, index) => (
          <div key={index}>
            {/* Render favorited items based on type */}
            {item.type === "mreco1" && (
              <MReco1 mangaReco={item} removeFromFavorites={removeFromFavorites} />
            )}
            {item.type === "mreco2" && (
              <MReco2 mangaReco2={item} removeFromFavorites={removeFromFavorites} />
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default FavoritePage;
