import MReco1 from "./MReco1.js";
import { Slide } from "react-slideshow-image";

const MangaData = [
  {
    name: "Get it Together",
    image: "reco/immortaldays.jpg",
  },
  {
    name: "After School Lessons for Unripe Apples",
    image: "reco/unripeapple.png",
  },
  {
    name: "Lookism",
    image: "reco/lookism.jpg",
  },
  {
    name: "The Boxer",
    image: "reco/theboxer.jpg",
  },
  {
    name: "Sweet Home",
    image: "reco/sweethome.jpg",
  },
  {
    name: "Eleceed",
    image: "reco/eleceed.jpg",
  },
  {
    name: "Ooh La La",
    image: "reco/oohlala.jpg",
  },
  {
    name: "Dwaejiuri (Pigpen)",
    image: "reco/dwaejuri.jpg",
  },
  {
    name: "Maybe Meant To Be",
    image: "reco/mmtb.jpg",
  },
  {
    name: "Something About Us",
    image: "reco/somthaboutus.jpg",
  },
  {
    name: "Get Schooled",
    image: "reco/getschooled.jpg",
  },
  {
    name: "Revelation of Youth",
    image: "reco/revelation.png",
  },
];

const image = ["props.mangaReco.image"];

function Recommendation() {
  return (
    <div className="reco">
      <ul>
        {MangaData.map((reco) => (
          <MReco1 mangaReco={reco} key={reco.name} />
        ))}
      </ul>
      <Slide>
        <div className="each-slide-effect">
          <div
            style={{ backgroundImage: `url(${image["props.mangaRec.image"]})` }}
          ></div>
        </div>
        <div className="each-slide-effect">
          <div style={{ backgroundImage: `url(${image[1]})` }}></div>
        </div>
        <div className="each-slide-effect">
          <div style={{ backgroundImage: `url(${image[2]})` }}></div>
        </div>
        <div className="each-slide-effect">
          <div style={{ backgroundImage: `url(${image[3]})` }}></div>
        </div>
      </Slide>
    </div>
  );
}

export default Recommendation;
