import "./App.css";
import "./styles.css";

function App() {
  return (
    <div className="App">
      <Header />
      <Slideshow />
    </div>
  );
}

function Header() {
  return (
    <div className="menu">
      <div className="logo">
        <img src="lezhin.png" alt="Lezhin Logo"></img>
      </div>
      <div className="menu-div">
        <ul>
          <button className="btn2">RECENT</button>
          <button className="btn2">SUBSCRIBED</button>
          <button className="btn2">DOWNLOADS</button>
          <button className="btn2">UNLOCKED</button>
          <button className="btn2">CREATORS</button>
          <button className="btn2">COMMENTS</button>
        </ul>
      </div>
    </div>
  );
}

const pic = ["solo.jpg", "boxer.jpg", "unripeapples.jpg", "operationlove.jpg"];
const delay = 2500;
function Slideshow() {
  const [index, setIndex] = React.useState(0);

  React.useEffect(() => {
    setTimeout (
      () => 
        setIndex((prevIndex) => 
        prevIndex === pic.length)
    )
  })


  return (
    <div className="slideshow">
      <div className="slideshowSlider"
      style={{ transform: `translate3d(${-index * 100}%, 0, 0)` }}>
        {pic.map((index) => (
          <div className="slide" key={index} />
        ))}
      </div>
      <div className="slideshowDots">
        {pic.map((_, idx) => (
          <div key={idx} className="slideshowDot"></div>
        ))}
      </div>
    </div>
  );
}

